system_prompts.content:
{
"schema": "http://json-schema.org/draft-07/schema#",
"definitions": {
"loginSchema": {
"type": "object",
"properties": {
"email": {
"type": "string",
"format": "email",
"errorMessage": {
"format": "Please enter a valid email address",
"required": "Required"
}
},
"password": {
"type": "string",
"errorMessage": {
"required": "Required",
"pattern": "Password is not valid"
},
"pattern": "^(?=.*[A-Za-z])(?=.*\\d)[A-Za-z\\d]{8,}"
}
},
"required": ["email", "password"]
},
"registrationSchema": {
"type": "object",
"properties": {
"acceptTermOfUse": {
"type": "boolean",
"const": true,
"errorMessage": "You should accept Terms & Conditions"
},
"firstName": {
"type": "string",
"errorMessage": "Required"
},
"lastName": {
"type": "string",
"errorMessage": "Required"
},
"passwordConfirmation": {
"type": "string",
"errorMessage": "Required"
},
"password": {
"type": "string",
"errorMessage": {
"required": "Required",
"pattern": "Password is not valid"
},
"pattern": "^(?=.[A-Za-z])(?=. \d)[A-Za-z\d]{8,}"
},
"email": {
"type": "string",
"format": "email",
"errorMessage": {
"format": "Please enter a valid email address",
"required": "Required"
}
}
},
"required": [
"acceptTermOfUse",
"firstName",
"lastName",
"passwordConfirmation",
"password",
"email"
]
},
"companyProfileSchema": {
"type": "object",
"properties": {
"billingAddress": {
"type": "string"
},
"billingPhone": {
"type": "string",
"minLength": 10,
"errorMessage": "Please enter a valid phone number."
},
"category": {
"type": "string",
"errorMessage": "Required"
},
"companyLogo": {},
"profileHeader": {},
"description": {
"type": "string",
"errorMessage": "Required"
},
"name": {
"type": "string",
"errorMessage": "Required"
},
"twitter": {
"type": "string"
},
"facebook": {
"type": "string"
},
"instagram": {
"type": "string"
},
"linkedin": {
"type": "string"
},
"website": {
"type": "string"
},
"yearFounded": {
"type": "string"
}
},
"required": [
"billingPhone",
"category",
"description",
"name"
]
},
"addEditSolutionSchema": {
"type": "object",
"properties": {
"name": {
"type": "string",
"errorMessage": "Required"
},
"category": {
"type": "string",
"errorMessage": "Required"
},
"comment": {
"type": "string",
"errorMessage": "Required"
},
"product-name": {
"type": "string"
},
"banner-description": {
"type": "string",
"maxLength": 70,
"errorMessage": "Description should be limited to 70 characters"
},
"product-details-url": {
"type": "string",
"format": "uri",
"errorMessage": "Please provide valid URL"
}
},
"required": ["name", "category", "comment"]
},
"updateProfileSchema": {
"type": "object",
"properties": {
"firstName": {
"type": "string",
"errorMessage": "Required"
},
"lastName": {
"type": "string",
"errorMessage": "Required"
},
"phone": {
"type": "string",
"minLength": 10,
"maxLength": 10,
"pattern": "^[0-9]+",
"errorMessage": {
"minLength": "Please enter a valid phone number.",
"maxLength": "Please enter a valid phone number.",
"pattern": "Please enter a valid phone number.",
"required": "Required"
}
}
},
"required": ["firstName", "lastName", "phone"]
},
"inviteUsersSchema": {
"type": "object",
"properties": {
"email": {
"type": "string",
"format": "email",
"errorMessage": {
"format": "Please enter a valid email address",
"required": "Required"
}
},
"department": {
"type": "string",
"errorMessage": "Required when email is present"
},
"email-1": {
"type": "string",
"format": "email",
"errorMessage": "Please enter a valid email address"
},
"department-1": {
"type": "string",
"errorMessage": "Required when email-1 is present"
},
"email-2": {
"type": "string",
"format": "email",
"errorMessage": "Please enter a valid email address"
},
"department-2": {
"type": "string",
"errorMessage": "Required when email-2 is present"
}
},
"required": ["email"]
}
}
}
