system_prompts.content:
{
  "resourceType": "Bundle",
  "type": "collection",
  "entry": [
    {
      "resource": {
        "resourceType": "Questionnaire",
        "id": "login-questionnaire",
        "status": "active",
        "title": "Login Form",
        "item": [
          {
            "linkId": "email",
            "text": "Email Address",
            "type": "string",
            "required": true,
            "validation": {
              "type": "email"
            },
            "errorMessage": "Please enter a valid email address"
          },
          {
            "linkId": "password",
            "text": "Password",
            "type": "string",
            "required": true,
            "validation": {
              "pattern": "^(?=.*[A-Za-z])(?=.*\\d)[A-Za-z\\d]{8,}"
            },
            "errorMessage": "Password must be at least 8 characters and contain letters and numbers"
          }
        ]
      }
    },
    {
      "resource": {
        "resourceType": "Questionnaire",
        "id": "registration-questionnaire",
        "status": "active", 
        "title": "User Registration",
        "item": [
          {
            "linkId": "firstName",
            "text": "First Name",
            "type": "string",
            "required": true
          },
          {
            "linkId": "lastName",
            "text": "Last Name", 
            "type": "string",
            "required": true
          },
          {
            "linkId": "email",
            "text": "Email Address",
            "type": "string",
            "required": true,
            "validation": {
              "type": "email"
            },
            "errorMessage": "Please enter a valid email address"
          },
          {
            "linkId": "password",
            "text": "Password",
            "type": "string",
            "required": true,
            "validation": {
              "pattern": "^(?=.*[A-Za-z])(?=.*\\d)[A-Za-z\\d]{8,}"
            },
            "errorMessage": "Password must be at least 8 characters and contain letters and numbers"
          },
          {
            "linkId": "passwordConfirmation",
            "text": "Confirm Password",
            "type": "string",
            "required": true
          },
          {
            "linkId": "acceptTermOfUse",
            "text": "Accept Terms & Conditions",
            "type": "boolean",
            "required": true,
            "errorMessage": "You should accept Terms & Conditions"
          }
        ]
      }
    },
    {
      "resource": {
        "resourceType": "Questionnaire",
        "id": "company-profile-questionnaire",
        "status": "active",
        "title": "Company Profile",
        "item": [
          {
            "linkId": "name",
            "text": "Company Name",
            "type": "string",
            "required": true
          },
          {
            "linkId": "category",
            "text": "Company Category",
            "type": "string",
            "required": true
          },
          {
            "linkId": "description",
            "text": "Company Description",
            "type": "text",
            "required": true
          },
          {
            "linkId": "billingPhone",
            "text": "Billing Phone Number",
            "type": "string",
            "required": true,
            "validation": {
              "minLength": 10
            },
            "errorMessage": "Please enter a valid phone number"
          },
          {
            "linkId": "billingAddress",
            "text": "Billing Address",
            "type": "string"
          },
          {
            "linkId": "yearFounded",
            "text": "Year Founded",
            "type": "string"
          },
          {
            "linkId": "socialMedia",
            "text": "Social Media Links",
            "type": "group",
            "item": [
              {
                "linkId": "twitter",
                "text": "Twitter Profile",
                "type": "string"
              },
              {
                "linkId": "facebook",
                "text": "Facebook Profile",
                "type": "string"
              },
              {
                "linkId": "instagram",
                "text": "Instagram Profile",
                "type": "string"
              },
              {
                "linkId": "linkedin",
                "text": "LinkedIn Profile",
                "type": "string"
              },
              {
                "linkId": "website",
                "text": "Company Website",
                "type": "string"
              }
            ]
          }
        ]
      }
    },
    {
      "resource": {
        "resourceType": "Questionnaire",
        "id": "solution-questionnaire",
        "status": "active",
        "title": "Add/Edit Solution",
        "item": [
          {
            "linkId": "name",
            "text": "Solution Name",
            "type": "string",
            "required": true
          },
          {
            "linkId": "category",
            "text": "Solution Category",
            "type": "string",
            "required": true
          },
          {
            "linkId": "comment",
            "text": "Solution Comment",
            "type": "text",
            "required": true
          },
          {
            "linkId": "productName",
            "text": "Product Name",
            "type": "string"
          },
          {
            "linkId": "bannerDescription",
            "text": "Banner Description",
            "type": "string",
            "validation": {
              "maxLength": 70
            },
            "errorMessage": "Description should be limited to 70 characters"
          },
          {
            "linkId": "productDetailsUrl",
            "text": "Product Details URL",
            "type": "string",
            "validation": {
              "type": "uri"
            },
            "errorMessage": "Please provide a valid URL"
          }
        ]
      }
    },
    {
      "resource": {
        "resourceType": "Questionnaire",
        "id": "profile-update-questionnaire",
        "status": "active",
        "title": "Update Profile",
        "item": [
          {
            "linkId": "firstName",
            "text": "First Name",
            "type": "string",
            "required": true
          },
          {
            "linkId": "lastName",
            "text": "Last Name",
            "type": "string",
            "required": true
          },
          {
            "linkId": "phone",
            "text": "Phone Number",
            "type": "string",
            "required": true,
            "validation": {
              "minLength": 10,
              "maxLength": 10,
              "pattern": "^[0-9]+"
            },
            "errorMessage": "Please enter a valid phone number"
          }
        ]
      }
    },
    {
      "resource": {
        "resourceType": "Questionnaire",
        "id": "invite-users-questionnaire",
        "status": "active",
        "title": "Invite Users",
        "item": [
          {
            "linkId": "email",
            "text": "Primary Email",
            "type": "string",
            "required": true,
            "validation": {
              "type": "email"
            },
            "errorMessage": "Please enter a valid email address"
          },
          {
            "linkId": "department",
            "text": "Primary Department",
            "type": "string",
            "required": true
          },
          {
            "linkId": "additionalInvites",
            "text": "Additional Invites",
            "type": "group",
            "item": [
              {
                "linkId": "email-1",
                "text": "Additional Email 1",
                "type": "string",
                "validation": {
                  "type": "email"
                },
                "errorMessage": "Please enter a valid email address"
              },
              {
                "linkId": "department-1",
                "text": "Department for Email 1",
                "type": "string"
              },
              {
                "linkId": "email-2",
                "text": "Additional Email 2",
                "type": "string",
                "validation": {
                  "type": "email"
                },
                "errorMessage": "Please enter a valid email address"
              },
              {
                "linkId": "department-2",
                "text": "Department for Email 2",
                "type": "string"
              }
            ]
          }
        ]
      }
    }
  ]
}
